import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-img',
  templateUrl: './img.component.html',
  styleUrls: ['./img.component.scss']
})
export class ImgComponent {
  @Input() img: string = ''
}
<img [src]="img" alt="Image not found" *ngIf="img">
<ng-template #elseImg>
  <img src="assets/images/image_not_found.jpeg">
</ng-template>
