import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  addTask() {
    this.todos.push(this.newTask);
    this.newTask = '';
  }

  todos: string[] = []
  newTask: string = ''
}
<h1>ngFor Dynamic</h1>

<label for="todo">Insert a new Task:</label> <br>
<input type="text" [(ngModel)]="newTask" (keyup)="keyHandler($event)">
<button (click)="addTask()">Add</button>

<ul *ngFor="let task of todos">
  <li>{{ task }}</li>
</ul>
