import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  onRegister() {
    console.log(this.registerInfo)
  }
  registerInfo = {
    username: '',
    email: '',
    password: ''
  }
}
<h1>Sign In Form</h1>

<form (ngSubmit)="onRegister()" #registerForm="ngForm">
  <label>Username</label>
  <span *ngIf="inputUsername.invalid">*</span>
  <input type="text" required name="username" [(ngModel)]="registerInfo.username" #inputUsername="ngModel" >
  <br>

  <label>E-mail</label>
  <span *ngIf="inputEmail.invalid">*</span>
  <input type="email" required name="email" #inputEmail="ngModel" [(ngModel)]="registerInfo.email">
  <br>

  <label>Password</label>
  <span *ngIf="inputPassword.invalid">*</span>
  <input type="password" required name="password" #inputPassword="ngModel" [(ngModel)]="registerInfo.password">
  <br>
  <br>
  <button [disabled]="registerForm.invalid" type="submit" >Submit</button>

</form>
