import { Component, OnInit, AfterViewChecked } from '@angular/core';
import { StoreService } from "../../services/store.service";

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.scss']
})
export class NavComponent implements OnInit, AfterViewChecked {
  cartItemsTotal: number = 0;

  constructor(
    private storeService: StoreService
  ) {
  }
  ngAfterViewChecked(): void {
    console.log('aaaaaa')
  }


  ngOnInit(): void {
    this.cartItemsTotal = this.storeService.getTotal();
  }
}
<ul>
  <li><a href="#home">Home</a></li>
  <li><a href="#news">News</a></li>
  <li><a href="#contact">Contact</a></li>
  <li style="float:right"><a class="active" href="#about">Cart #{{ cartItemsTotal }}</a></li>
</ul>
