import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  user = {
    name : '',
    age : 0
  };
}
<h1>ngIf</h1>

<label for="name">Insert your username: </label>
<input type="text" [(ngModel)]="user.name">

<br>
<span *ngIf="user.name.toUpperCase() === 'ADMIN'" style="color: red;">Invalid username</span>
