import { Component } from '@angular/core';
import { Fumo } from "./fumo.model";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  fumoProducts: Fumo[] = [
    {
      name : "Yuyuko",
      price : 80,
      image : "./assets/images/yuyukoFumo.jpg"
    },
    {
      name : "Cirno",
      price : 50,
      image : "./assets/images/cirnoFumo.jpg"
    },
    {
      name : "Reimu",
      price : 65,
      image : "./assets/images/reimuFumo.jpg"
    },
    {
      name : "Marisa",
      price : 65,
      image : "./assets/images/marisaFumo.jpg"
    },
    {
      name : "Scarlet",
      price : 55,
      image : "./assets/images/scarletFumo.jpg"
    }
  ];
}
<h1>Fumo Product List</h1>
<hr>

<div class="fumoGrid">
  <div *ngFor="let fumo of fumoProducts">
    <h3>{{ fumo.name }} ${{ fumo.price }}</h3>
    <img [src]="fumo.image" alt={{fumo.name}}>
  </div>
</div>
