import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  user = {
    name : '',
    age : 0
  };
}
<h1>ngModel</h1>

<label for="username">Insert your username: </label>
<input type="text" required #nameInput="ngModel" [(ngModel)]="user.name">
<br>
<p>Is name valid? {{ nameInput.valid }}</p>


<br><br><br>

<label for="age">Insert your age: </label>
<input type="number" min="0" max="99" #ageInput="ngModel" [(ngModel)]="user.age">
<br>
<p>Is age valid? {{ ageInput.valid }} </p>
