import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  deleteTask(index: number) {
    this.todos.splice(index, 1);

  }

  keyHandler() {
    this.addTask()
  }

  addTask() {
    this.todos.push(this.newTask);
    this.newTask = '';
  }

  todos: string[] = []
  newTask: string = ''
}
<h1>ngFor With Complex Objects</h1>

<label for="todo">Insert a new Task:</label> <br>
<input type="text" [(ngModel)]="newTask" (keyup.Enter)="keyHandler()">
<button (click)="addTask()">Add</button>

<h2 *ngIf="todos.length === 0">Please insert tasks</h2>
<ul *ngFor="let task of todos; index as i">
  <li>{{ task }} <span (click)="deleteTask(i)" style="color: red;">[ DELETE ]</span> </li>
</ul>
